using UnityEngine;using System.Collections;[RequireComponent (typeof(SpriteRenderer))]public class TurnIndicator : MonoBehaviour {    public Sprite leftTurnSprite;    public Sprite rightTurnSprite;    public Sprite straightSprite;    SpriteRenderer sprite;    void Awake()    {        sprite = GetComponent<SpriteRenderer>();    }    public void TurnLeft()    {        sprite.sprite = leftTurnSprite;        sprite.enabled = true;    }    public void TurnRight()    {        sprite.sprite = rightTurnSprite;        sprite.enabled = true;    }    public void GoStraight()    {        sprite.sprite = straightSprite;        sprite.enabled = true;    }    public void DoneTurn()    {        sprite.enabled = false;    }}